mapper.delete:
delete from

where 

		and ID!=-1=#{ID!=-1}
	
	
		and BILLNO=#{BILLNO}

mapper.select:
select 

from 

where  1=1 
	
		and ID!=-1=#{ID!=-1}
	
	
		and BILLNO=#{BILLNO}

SELECT torder.ID,torder.BILLNO,torder.ORDERSTATUSID,torder.PAYMENTSTATUSID, torder.PLUCOUNT,torder.TOTALPRICE,torder.PAYMENTDATE,torder.CREATEDATE, torder.STOREID,torder.WECHATNAME,torder.OPENID,torder.REMARKS, (SELECT tcproperty.`name` FROM tcproperty WHERE tcproperty.ID=torder.orderStatusID) AS STATUSNAME, (SELECT tcproperty.`name` FROM tcproperty WHERE tcproperty.ID=torder.payMentStatusID) AS PAYSTATUSNAME, (SELECT tstore.storeName FROM tstore WHERE tstore.id=torder.storeId) AS STORENAME, t_member.phoneNumber as PHONENUMBER from torder LEFT JOIN t_member ON torder.openId=t_member.openId where 1=1
and ( BILLNO LIKE CONCAT(CONCAT('', #{pd.keywords}),'%') OR WECHATNAME LIKE CONCAT(CONCAT('', #{pd.keywords}),'%') OR SHELVESNAME LIKE CONCAT(CONCAT('', #{pd.keywords}),'%') OR PLACENAME LIKE CONCAT(CONCAT('', #{pd.keywords}),'%') )
and STOREID=#{pd.storeId}
and payMentStatusID=#{pd.payMentStatusID}
and orderStatusID=#{pd.orderStatusID}
AND DATE_FORMAT(CREATEDATE,'%Y-%m-%d') >=#{pd.lastStart}
AND DATE_FORMAT(CREATEDATE,'%Y-%m-%d') <= #{pd.lastEnd}
order by CREATEDATE desc

SELECT torder.ID,torder.BILLNO,torder.ORDERSTATUSID,torder.PAYMENTSTATUSID, torder.PLUCOUNT,torder.TOTALPRICE,torder.PAYMENTDATE,torder.CREATEDATE, torder.STOREID,torder.WECHATNAME,torder.OPENID,torder.REMARKS, (SELECT tcproperty.`name` FROM tcproperty WHERE tcproperty.ID=torder.orderStatusID) AS STATUSNAME, (SELECT tcproperty.`name` FROM tcproperty WHERE tcproperty.ID=torder.payMentStatusID) AS PAYSTATUSNAME, (SELECT tstore.storeName FROM tstore WHERE tstore.id=torder.storeId) AS STORENAME, t_member.phoneNumber as PHONENUMBER from torder LEFT JOIN t_member ON torder.openId=t_member.openId where 1=1
and ( BILLNO LIKE CONCAT(CONCAT('', #{keywords}),'%') OR WECHATNAME LIKE CONCAT(CONCAT('', #{keywords}),'%') OR SHELVESNAME LIKE CONCAT(CONCAT('', #{keywords}),'%') OR PLACENAME LIKE CONCAT(CONCAT('', #{keywords}),'%') )
and STOREID=#{storeId}
and payMentStatusID=#{payMentStatusID}
and orderStatusID=#{orderStatusID}
AND DATE_FORMAT(CREATEDATE,'%Y-%m-%d') >=#{lastStart}
AND DATE_FORMAT(CREATEDATE,'%Y-%m-%d') <= #{lastEnd}
order by CREATEDATE desc
