mapper.select:
select

from 

where 1=1

	
		and 
		(reservationNumber LIKE CONCAT(CONCAT('', #{pd.keywords}),'%')
		 or
		 reservationName LIKE CONCAT(CONCAT('', #{pd.keywords}),'%')
		 or
		 reservedNumber LIKE CONCAT(CONCAT('', #{pd.keywords}),'%')
		 or
		 proName LIKE CONCAT(CONCAT('', #{pd.keywords}),'%')
		)
	
	
		and memberId=#{pd.memberId}
	
	
		and reservationType=#{pd.reservationType}
	
	
		and reservationStateID=#{pd.reservationStateID}
	
	
  AND  DATE_FORMAT(createDate,'%Y-%m-%d') >=#{pd.lastStart}
	
	
  AND  DATE_FORMAT(createDate,'%Y-%m-%d') <= #{pd.lastEnd} 
	

order by createDate desc

select
from
where 1=1
and (reservationNumber LIKE CONCAT(CONCAT('', #{keywords}),'%') or reservationName LIKE CONCAT(CONCAT('', #{keywords}),'%') or reservedNumber LIKE CONCAT(CONCAT('', #{keywords}),'%') or proName LIKE CONCAT(CONCAT('', #{keywords}),'%') )
and reservationType=#{reservationType}
and reservationStateID=#{reservationStateID}
AND DATE_FORMAT(createDate,'%Y-%m-%d') >=#{lastStart}
AND DATE_FORMAT(createDate,'%Y-%m-%d') <= #{lastEnd}
order by createDate desc
