mapper.select:
select

from 

where 1=1

	
		and 
		(carNum LIKE CONCAT(CONCAT('', #{pd.keywords}),'%')
		 or
		 newCarNum LIKE CONCAT(CONCAT('', #{pd.keywords}),'%')
		)
	
	
		and storeName=#{pd.storeName}
	
	
		and vipName=#{pd.vipName}
	
	
		and status=#{pd.status}
	
	
  AND  DATE_FORMAT(createDate,'%Y-%m-%d') >=#{pd.lastStart}
	
	
  AND  DATE_FORMAT(createDate,'%Y-%m-%d') <= #{pd.lastEnd} 
	

order by createDate desc
