mapper.select:
SELECT tstore.*,(SELECT tcproperty.`name` FROM tcproperty where tcproperty.id=tstore.storeStatusID) as storeStatusName from tstore
where 1=1

	
		and 
		(storeName LIKE CONCAT(CONCAT('', #{pd.keywords}),'%')
		 or
		 storeDistrict LIKE CONCAT(CONCAT('', #{pd.keywords}),'%')
		 or
		 storeAddress LIKE CONCAT(CONCAT('', #{pd.keywords}),'%')
		)
	
	
		and storeStatusID=#{pd.storeStatusID}
	
	
	
  AND  DATE_FORMAT(createDate,'%Y-%m-%d') >=#{pd.lastStart}
	
	
  AND  DATE_FORMAT(createDate,'%Y-%m-%d') <= #{pd.lastEnd} 
	


order by createDate desc
